import React, {useEffect, useState} from "react";

function useDarkMode() {
	const [darkMode, setDarkMode] = useState(() => {
		const mode = localStorage.getItem("darkMode");
		return mode === "true" ? true : false;
	});

	useEffect(() => {
		localStorage.setItem("darkMode", darkMode);
		setCSSVariables(darkMode);
	}, [darkMode]);

	return [darkMode, setDarkMode];
}

function setCSSVariables(darkMode) {
	const root = document.documentElement;

	if (darkMode) {
		root.style.setProperty("--primary", "#ffffff");
		root.style.setProperty("--secondary", "#f54ea2");
		root.style.setProperty("--tertiary", "#6185a5");
		root.style.setProperty("--dark", "#08071C");
		root.style.setProperty("--amarillo", "#d5d5d5");
		root.style.setProperty("--hueso", "#f8f9fe");
		root.style.setProperty("--blanco", "#fff");
		root.style.setProperty("--blancoAzulado", "#7F9CB5");
		root.style.setProperty("--verde", "#ffffff");
		root.style.setProperty("--negro", "#ffffff");
		root.style.setProperty("--facebook", "#4267b2");
		root.style.setProperty("--twitter", "#1DA1F2");
		root.style.setProperty("--youtube", "#ff0000");
		root.style.setProperty("--pinterest", "#E60023");
		root.style.setProperty("--github", "#171515");
	} else {
		root.style.setProperty("--primary", "#38d16a");
		root.style.setProperty("--secondary", "#f54ea2");
		root.style.setProperty("--tertiary", "#6185a5");
		root.style.setProperty("--dark", "#08071C");
		root.style.setProperty("--amarillo", "#f8f9fe");
		root.style.setProperty("--hueso", "#d5d5d5");
		root.style.setProperty("--blanco", "#fff");
		root.style.setProperty("--blancoAzulado", "#7F9CB5");
		root.style.setProperty("--verde", "#38d16a");
		root.style.setProperty("--negro", "#000");
		root.style.setProperty("--facebook", "#4267b2");
		root.style.setProperty("--twitter", "#1DA1F2");
		root.style.setProperty("--youtube", "#ff0000");
		root.style.setProperty("--pinterest", "#E60023");
		root.style.setProperty("--github", "#171515");
	}
}

function DarkModeSwitch() {
	const [darkMode, setDarkMode] = useDarkMode();

	return (
		<div>
			<button className="primary-btn btn-switch" onClick={() => setDarkMode(!darkMode)}>
				{darkMode ? "Light Mode" : "Dark Mode"}
			</button>
		</div>
	);
}

export default DarkModeSwitch;
